package com.dnb;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;
import static org.junit.jupiter.api.Assertions.assertTrue;

class Base64EncoderAndDecoderTest {

    private final Base64EncoderAndDecoder encoderAndDecoder = new Base64EncoderAndDecoder();

    @Test
    void testBase64Encode() {
        encoderAndDecoder.base64DecodeAndEncode("Qm9lZkJBRjE5NFFXMG9mQVdhc3NhNTQ4NDZOR0ZTR0k=");
        encoderAndDecoder.base64DecodeAndEncode("");
        encoderAndDecoder.base64DecodeAndEncode("");
        assertTrue(true);
    }

    @Test
    void test20210511Cit2Test() {
        encoderAndDecoder.base64DecodeAndEncode("VHNqOFc4dFowOGRlbldlSDc5ck51Vk8wckc5Yjh1N2s=");
        assertTrue(true);
    }

    @Test
    void test20210410Frd() {
        encoderAndDecoder.base64DecodeAndEncode("NTLM authorization, komt meerdere keren voor", "TlRMTVNTUAABAAAAl4II4gAAAAAAAAAAAAAAAAAAAAAKAGNFAAAADw==");
        encoderAndDecoder.base64DecodeAndEncode("Hans authorization", "TlRMTVNTUAABAAAAl4II4gAAAAAAAAAAAAAAAAAAAAAKAGNFAAAADw==");
        encoderAndDecoder.base64DecodeAndEncode("Stephan authorization (2021-03-31 10:44:36,667):", "");
        encoderAndDecoder.base64DecodeAndEncode("Marnix authorization: (2021-03-31 10:47:02,262)", "");
        encoderAndDecoder.base64DecodeAndEncode("Marnix authorization: (2021-03-31 10:47:02,465)", "");
        encoderAndDecoder.base64DecodeAndEncode("Marnix authorization: (2021-03-31 10:47:09,137)", "");
        encoderAndDecoder.base64DecodeAndEncode("Marnix Authorization: (2021-04-06 13:42:07,488)", "");
        assertTrue(true);
    }

    @Test
    void test20210410FrdNtlmAuth() {
        encoderAndDecoder.base64DecodeAndEncode("NTLM authorization, komt meerdere keren voor", "TlRMTVNTUAABAAAAl4II4gAAAAAAAAAAAAAAAAAAAAAKAGNFAAAADw==");
        assertTrue(true);
    }

    @Test
    void testEncodePasswordDia() {
        String diaPassWordToEncode = "cyxzTzBAaV94akU+";
        String encoded = encoderAndDecoder.base64Encode(diaPassWordToEncode);
        String decoded = encoderAndDecoder.base64Decode(diaPassWordToEncode);
        assertEquals("Y3l4elR6QkFhVjk0YWtVKw==", encoded);
        assertEquals("s,sO0@i_xjE>", decoded);
    }

}
